const crypto = require('crypto')

module.exports = async function handler(service, event, utils) {
  const {proxies, config, log: logger} = service
  const {TEMPLATES_CRUD_NAME} = config

  logger.info({event}, 'Received event')

  const {payload} = event
  const {recipient, channel, templateName, emailObject, emailHtmlMessage, messageContent} = payload

  const [user] = await utils.getRecipients(service, {ids: [recipient]})
  if (!user) {
    logger.warn({recipient}, 'User not found')
    return []
  }
  logger.info({user}, 'User found')

  // Send email or sms using template
  if (templateName) {
    logger.info({templateName}, 'Sending notification using template')
    const notification = await utils.sendMessages(service, event, user, [{channel, templateName}])
    return [notification]
  }

  // Send email or sms using custom fields
  logger.info({emailObject, emailHtmlMessage, messageContent}, 'Sending notification using custom fields')
  const tempTemplateName = crypto.randomUUID().toString()
  const tempTemplate = {
    name: tempTemplateName,
    ...(emailObject && {emailTitle: emailObject}),
    ...(emailHtmlMessage && {emailHtmlMessage}),
    ...(messageContent && {smsMessage: messageContent}),
  }
  const {payload: {_id: tempTemplateId}} = await proxies.crudProxy.post(`/${TEMPLATES_CRUD_NAME}/`, tempTemplate)
  logger.info({_id: tempTemplateId, template: tempTemplate}, 'Created temporary template')
  const notification = await utils.sendMessages(service, event, user, [{channel, templateName: tempTemplateName}])
  await proxies.crudProxy.delete(`/${TEMPLATES_CRUD_NAME}/${tempTemplateId}`, {}, {returnAs: 'BUFFER'})
  logger.info({_id: tempTemplateId}, 'Deleted temporary template')
  return [notification]
}

module.exports = async function handler(service, event, utils) {
  const { config, log: logger, configMap: configuration, proxies } = service
  const { TEMPLATES_CRUD_NAME } = config
  const { payload } = event
  const { eventName, templateName, recipient: usersGroup, channels } = payload
  logger.debug({event}, 'Receive event')

  // retrieve users with group
  const users = await utils.getRecipients(service, { groups: [usersGroup] })
  logger.debug({ users: users.length }, 'Retrieve users')
  // retrieve template
  const { payload: templates } = await get(
    proxies.crudProxy,
    TEMPLATES_CRUD_NAME,
    { name: templateName },
    logger
  )
  logger.debug({ templates: templates[0] }, 'Fetched template')
  const [template] = templates

  // for each user send messages to all channels in template
  const notifications = users.map(async (user) => {
    const messages = []
    if (isEmailChannel(channels, template, configuration)) {
      messages.push({channel: 'email', templateName})
      logger.debug('Send email')
    }
    if (isPushChannel(channels, template, configuration)) {
      messages.push({channel: 'push', templateName})
      logger.debug('Send push notification')
    }
    if (isSmsChannel(channels, template, configuration)) {
      messages.push({channel: 'sms', templateName})
      logger.debug('Send sms')
    }
    if (isVoiceChannel(channels, template, configuration)) {
      messages.push({channel: 'voice', templateName})
      logger.debug('Send voice message')
    }
    if (isWhatsappChannel(channels, template, configuration)) {
      messages.push({channel: 'whatsapp', templateName})
      logger.debug('Send whatsapp message')
    }
    const eventWithCustomName = {
      ...event,
      name: eventName,
    }
    const messagesNotification = await utils.sendMessages(service, eventWithCustomName, user, messages)
    return utils.mergeNotifications(service, event, user, [messagesNotification])
  })
  return awaitAll(notifications, event, users)
}

async function get(crudProxy, crudCollection, query, logger) {
  const path = `/${crudCollection}/`
  logger.trace({ query }, `Start GET ${path}`)
  const response = await crudProxy.get(path, { query })
  logger.trace(
    { statusCode: response.statusCode, payload: response.payload?.length },
    `End GET ${path}`
  )
  return response
}

function isChannelActive(channel, configuration) {
  return configuration.activeChannels?.includes(channel)
}

function isEmailChannel(channels, template, configuration) {
  const { emailTitle, emailHtmlMessage, emailMessage, emailAttachments } = template
  return (
    channels.includes('email') && isChannelActive('email', configuration) &&
    (emailTitle || emailHtmlMessage || emailMessage || emailAttachments)
  )
}

function isPushChannel(channels, template, configuration) {
  const { pushTitle, pushSubtitle, pushMessage, pushData } = template
  return (
    channels.includes('push') && isChannelActive('push', configuration) && (pushTitle || pushSubtitle || pushMessage || pushData)
  )
}

function isSmsChannel(channels, template, configuration) {
  const { smsMessage } = template
  return channels.includes('sms') && isChannelActive('sms', configuration) && smsMessage
}

function isVoiceChannel(channels, template, configuration) {
  const { voiceMessage } = template
  return channels.includes('voice') && isChannelActive('voice', configuration) && voiceMessage
}

function isWhatsappChannel(channels, template, configuration) {
  const { whatsappTemplateName, whatsappTemplateParameters } = template
  return (
    channels.includes('whatsapp') && isChannelActive('whatsapp', configuration) &&
    (whatsappTemplateName || whatsappTemplateParameters)
  )
}

async function awaitAll(userNotifications, event, users) {
  return (await Promise.allSettled(userNotifications)).map((userNotification, index) => {
    return userNotification.status === 'fulfilled' ?
      userNotification.value :
      {
        event,
        recipient: users[index].id,
        failures: [userNotification.reason.message || userNotification.reason.payload || JSON.stringify(userNotification.reason)],
      }
  })
}

const { DateTime } = require('luxon')

module.exports = async function handler(service, event, utils) {
  const { log: logger } = service
  const { payload: monitoring, metadata } = event
  if (!monitoring) {
    const message = 'Cannot process the event since monitoring payload is missing'
    logger.error({ event }, message)
    throw new Error(message)
  }

  // Se nel monitoring Ã¨ definito hours usare nome dell'evento custom MultiDailyMonitoringReminder
  const reminderEvent = {
    ...event,
    name: monitoring.hours ? 'MultiDailyMonitoringReminder' : 'TMM/MonitoringReminder/v1',
  }

  const { doctorId, patientId } = monitoring
  const participants = new Set([doctorId, patientId])
  metadata?.includeUsers?.forEach((participant) => participants.add(participant))
  metadata?.excludeUsers?.forEach((participant) => participants.delete(participant))

  const users = await utils.getRecipients(service, { ids: Array.from(participants) })
  logger.debug({ users: users.length }, 'Fetched participants')

  const notifications = users.map(async (recipient) => {
    const userNotifications = []
    const notificationSettings = await utils.getNotificationSettings(service, event, recipient)
    const mergedNotificationSettings = utils.mergeNotificationSettings(
      service,
      notificationSettings
    )
    const messagesToSend = utils.buildMessages(service, mergedNotificationSettings)
    const messagesNotification = await utils.sendMessages(service, event, recipient, messagesToSend)
    userNotifications.push(messagesNotification)

    logger.debug({ messagesNotification: messagesNotification.length }, 'messagesNotification')

    const nextRemindersDateTimes = getMultipleDailyReminderEvents(monitoring)

    logger.debug({ monitoring }, 'monitoring')
    logger.debug({ nextRemindersDateTimes }, 'nextRemindersDateTimes')

    if (nextRemindersDateTimes.length) {
      const reminderNotificationsPromises = nextRemindersDateTimes.map(nextReminderDateTime => {
        const notificationReminder = utils.buildReminders(
          service,
          recipient,
          mergedNotificationSettings,
          nextReminderDateTime
        )
        logger.debug({ notificationReminder }, 'notificationReminder')

        return utils.setReminders(
          service,
          reminderEvent,
          recipient,
          notificationReminder
        )
      })

      const reminderNotifications = await Promise.all(reminderNotificationsPromises)
      reminderNotifications.forEach(reminderNotification => userNotifications.push(reminderNotification))
    }

    logger.debug({ userNotifications }, 'userNotifications')
    return utils.mergeNotifications(service, event, recipient, userNotifications)
  })

  return awaitAll(notifications, event, users)
}

/**
 * Return a ISO 8601 date-time string representing the given date/time in a given time zone (default: UTC)
 * @param {DateTime} dateTime Date/time
 * @param {string} timeZone A time zone (see https://moment.github.io/luxon/#/zones?id=specifying-a-zone for a list of supported values)
 * @returns {string} A ISO 8601 date-time string representing the given date/time in the given time zone (default: UTC)
 */
const toISO = (dateTime, timeZone = 'utc') => dateTime.setZone(timeZone).toISO()

/**
 * Convert a string representing an ISO 8601 date-time into a UTC date/time object
 * @param {string} isoDateTime ISO 8601 date-time string
 * @returns {DateTime} A UTC date/time object
 */
const fromISOtoUTC = (isoDateTime) => DateTime.fromISO(isoDateTime, { zone: 'utc' })

/**
 * Returns current date/time in UTC
 * @returns {DateTime} Current date/time in UTC
 */
const currentDateTime = () => DateTime.utc()

/**
 * Return the next event, at least one day from now, of the monitoring plan
 *
 * @param {Monitoring | Therapy} monitoring A monitoring or therapy plan
 * @returns {string | undefined} Next day of the monitoring or therapy plan, if available, undefined otherwise
 */
function getNextReminderEvent(monitoring) {
  const { each, startDate, endDate } = monitoring
  if (each && each.length > 0) {
    const sendAfter = currentDateTime().endOf('day')
    let sendAt = fromISOtoUTC(startDate).startOf('day')
    while (sendAt < sendAfter) {
      sendAt = sendAt.plus({ day: 1 })
    }
    if (!each?.includes('day')) {
      const allowedWeekDays = each?.map((weekDay) => luxonWeekDays.get(weekDay))
      while (!allowedWeekDays?.includes(sendAt.weekday)) {
        sendAt = sendAt.plus({ day: 1 })
      }
    }
    if (!endDate || sendAt < fromISOtoUTC(endDate)) {
      return toISO(sendAt)
    }
  }
}

/**
 * Return the list of next events, at least one day from now, of the monitoring plan
 *
 * @param {Monitoring | Therapy} monitoring A monitoring or therapy plan
 * @returns {string | undefined} Next day of the monitoring or therapy plan, if available, undefined otherwise
 */
function getMultipleDailyReminderEvents(monitoring) {
  const { each, startDate, endDate, hours } = monitoring

  if (!each || each.length === 0) {
    return []
  }

  const reminderEventsDateTimes = []
  if (!hours || hours.length === 0) {
    console.log('hours is not defined')
    const nextReminderEvent = getNextReminderEvent(monitoring)
    console.log(nextReminderEvent, 'the next reminder event, hours is not defined')
    return nextReminderEvent ? [nextReminderEvent] : []
  }

  console.log('hours is defined')
  hours.forEach((reminderHour) => {
    const sendAfter = currentDateTime().endOf('day')
    console.log({ reminderHour }, 'inside hours loop, print one reminder hour')
    const [hour, minute] = reminderHour.split(':').map(Number)
    console.log({ hour, minute }, 'hours and minutes')

    let sendAt = fromISOtoUTC(startDate).set({ hour, minute })

    while (sendAt < sendAfter) {
      sendAt = sendAt.plus({ day: 1 })
    }
    if (!each?.includes('day')) {
      const allowedWeekDays = each?.map((weekDay) => luxonWeekDays.get(weekDay))
      while (!allowedWeekDays?.includes(sendAt.weekday)) {
        sendAt = sendAt.plus({ day: 1 })
      }
    }
    if (!endDate || sendAt < fromISOtoUTC(endDate)) {
      reminderEventsDateTimes.push(toISO(sendAt))
    }
  })
  return reminderEventsDateTimes
}

async function awaitAll(userNotifications, event, users) {
  return (await Promise.allSettled(userNotifications)).map((userNotification, index) => {
    return userNotification.status === 'fulfilled'
      ? userNotification.value
      : {
          event,
          recipient: users[index].id,
          failures: [
            userNotification.reason.message ||
              userNotification.reason.payload ||
              JSON.stringify(userNotification.reason),
          ],
        }
  })
}

const luxonWeekDays = new Map([
  ['monday', 1],
  ['tuesday', 2],
  ['wednesday', 3],
  ['thursday', 4],
  ['friday', 5],
  ['saturday', 6],
  ['sunday', 7],
])

const {DateTime} = require('luxon')

module.exports = async function handler(service, event, utils) {
  const {log: logger} = service
  const {payload: monitoring, metadata} = event
  if (!monitoring) {
    const message = 'Cannot process the event since monitoring payload is missing'
    logger.error({event}, message)
    throw new Error(message)
  }

  const recipients = metadata?.reminder?.recipients || []
  const schedule = metadata?.reminder?.schedule
  const oldExpirationDatetime = metadata?.reminder.expirationDate
  if (recipients.length === 0) return []

  const users = await utils.getRecipients(service, {ids: Array.from(recipients)})
  logger.debug({users: users.length}, 'Fetched participants')

  const nextMonitoringEvent = getNextReminderWithHourEvent(monitoring, oldExpirationDatetime)
  logger.debug({nextMonitoringEvent}, 'Next reminder to schedule')

  const notifications = users.map(async recipient => {
    const notificationSettings = (await utils.getNotificationSettings(service, event, recipient))
      .filter(setting => !schedule || setting.reminders?.includes(schedule))

    const mergedNotificationSettings = utils.mergeNotificationSettings(service, notificationSettings)
    const messagesToSend = utils.buildMessages(service, mergedNotificationSettings)
    logger.debug({messagesToSend}, 'messagesToSend')

    const messagesNotification = await utils.sendMessages(service, event, recipient, messagesToSend)
    logger.debug({messagesNotification}, 'messagesNotification')

    const userNotifications = [messagesNotification]

    if (nextMonitoringEvent) {
      const nextReminder = buildNextDatetimeReminder(service, recipient, nextMonitoringEvent)
      const reminderNotifications = await utils.setReminders(service, event, recipient, [nextReminder])
      logger.debug({ reminderNotifications }, 'reminderNotifications')

      userNotifications.push(reminderNotifications)
    }

    logger.debug({ userNotifications }, 'userNotifications')
    return utils.mergeNotifications(service, event, recipient, userNotifications)
  })

  return awaitAll(notifications, event, users)
}


/**
 * Return a ISO 8601 date-time string representing the given date/time in a given time zone (default: UTC)
 * @param {DateTime} dateTime Date/time
 * @param {string} timeZone A time zone (see https://moment.github.io/luxon/#/zones?id=specifying-a-zone for a list of supported values)
 * @returns {string} A ISO 8601 date-time string representing the given date/time in the given time zone (default: UTC)
 */
const toISO = (dateTime, timeZone = 'utc') => dateTime.setZone(timeZone).toISO()

/**
 * Convert a string representing an ISO 8601 date-time into a UTC date/time object
 * @param {string} isoDateTime ISO 8601 date-time string
 * @returns {DateTime} A UTC date/time object
 */
const fromISOtoUTC = (isoDateTime) => DateTime.fromISO(isoDateTime, {zone: 'utc'})

/**
 * Returns current date/time in UTC
 * @returns {DateTime} Current date/time in UTC
 */
const currentDateTime = () => DateTime.utc()


/**
 * Return the next event, at least one day from now, of the monitoring plan, at the specified hour and minute
 *
 * @param {Monitoring | Therapy} monitoring A monitoring or therapy plan
 * @param {string} oldExpirationDatetime the expiration datetime of the current reminder
 * @returns {string | undefined} Next day of the monitoring or therapy plan, if available, undefined otherwise
 */
function getNextReminderWithHourEvent(monitoring, oldExpirationDatetime) {
  const {each, endDate, hours} = monitoring
  if (!each || each.length === 0) {
    return
  }
  if (!hours || hours.length === 0) {
    console.log('hours is not defined')
    return
  }

  if (each && each.length > 0) {
    const sendAfter = currentDateTime().endOf('day')
    let sendAt = fromISOtoUTC(oldExpirationDatetime)
    console.log({oldExpirationDatetime}, 'oldExpirationDatetime')

    while (sendAt < sendAfter) {
      sendAt = sendAt.plus({day: 1})
    }
    if (!each?.includes('day')) {
      const allowedWeekDays = each?.map(weekDay => luxonWeekDays.get(weekDay))
      while (!allowedWeekDays?.includes(sendAt.weekday)) {
        sendAt = sendAt.plus({day: 1})
      }
    }
    console.log({sendAt}, 'sendAt - before returning the next reminder')

    if (!endDate || sendAt < fromISOtoUTC(endDate)) {
      return toISO(sendAt)
    }
  }
}

/**
 * It builds the reminder for the next expirationDateTime
 *
 * @param {customPlugin.DecoratedFastify} service The decorated Fastify instance
 * @param {CrudRecipient} user User details about the notification recipient
 * @param {string} eventDateTime The scheduled date-time of the event
 * @returns {NotificationReminder} The reminder specifications
 */
const buildNextDatetimeReminder = (service, user, eventDateTime) => {
  const {log: logger} = service
  const reminder = {
    schedule: 'PT0S',
    expirationDate: eventDateTime,
    recipients: [user.id],
    triggers: [],
  } 
  logger.debug({reminder}, `Built one reminder`)
  return reminder
}

