/**
 * Schedule the reminder for the given event and user and return a notification with the details
 *
 * @async
 * @param {customPlugin.DecoratedFastify} service the decorated fastify instance
 * @param {CrudEvent} event the notification event
 * @param {CrudRecipient} user the user details about the notification recipient
 * @param {NotificationReminder} reminder the notification reminder
 * @returns {Promise<Notification>} the notification with the details about the reminder processed
 */
const setReminder = async(service, event, user, reminder) => {
  const {CRUD_SERVICE_URL, TIMER_SERVICE_URL, SERVICE_NAME, NOTIFICATIONS_CRUD_NAME} = service.config
  const {log: logger} = service
  const reminderEvent = pick(event, Object.keys(notificationEventSchema.properties))

  const timerClient = service.getHttpClient(TIMER_SERVICE_URL)

  const {expirationDate} = reminder
  const metadata = {
    ...reminderEvent.metadata,
    reminder,
  }
  const scheduleBody = {
    payload: {
      ...reminderEvent,
      metadata,
    },
    startDate: expirationDate,
    expirationIntervalMs: 1,
    applicantService: 'notification-manager',
    outputMode: {
      type: 'rest',
      method: 'post',
      protocol: 'http',
      hostname: SERVICE_NAME,
      path: '/notification-events/',
    },
  }
  const processedReminder = await timerClient.post('/schedule', scheduleBody)

  const body = {
    event: reminderEvent,
    recipient: user.id,
    reminders: [processedReminder],
  }
  if (processedReminder) {
    const crudClient = service.getHttpClient(CRUD_SERVICE_URL)
    await post(crudClient, NOTIFICATIONS_CRUD_NAME, body, logger)
  }

  return body
}


