[
  {
    "identifier": "bodyTemperature",
    "type": "measurement",
    "name": {
      "en": "Body Temperature",
      "it": "Temperatura Corporea"
    },
    "schema": {
      "type": "object",
      "properties": {
        "bodyTemperature": {
          "type": "number",
          "minimum": 31,
          "maximum": 45
        }
      },
      "required": [
        "bodyTemperature"
      ]
    },
    "labels": {
      "bodyTemperature": {
        "en": "Body temperature [°C]",
        "it": "Temperatura corporea [°C]"
      }
    }
  },
  {
    "identifier": "cardiologyTherapy",
    "type": "therapy",
    "name": {
      "it": "Terapia Cardiologica",
      "en": "Cardiology Therapy"
    },
    "schema": {
      "type": "object",
      "properties": {
        "drug": {
          "type": "string"
        },
        "dosage": {
          "type": "string"
        },
        "notes": {
          "type": "string"
        }
      }
    },
    "labels": {
      "drug": {
        "en": "Drug",
        "it": "Farmaco"
      },
      "dosage": {
        "en": "Dosage",
        "it": "Dosaggio"
      },
      "notes": {
        "en": "Notes",
        "it": "Note"
      }
    }
  },
  {
    "identifier": "physicalExercise",
    "type": "therapy",
    "name": {
      "en": "Physical Exercise",
      "it": "Esercizio Fisico"
    },
    "schema": {
      "type": "object",
      "properties": {
        "sessionName": {
          "type": "string"
        },
        "advices": {
          "type": "string"
        },
        "tool": {
          "type": "string"
        },
        "exerciseType": {
          "type": "string"
        },
        "steps": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "duration": {
                "type": "integer"
              },
              "power": {
                "type": "integer"
              }
            }
          }
        }
      }
    },
    "labels": {
      "sessionName": {
        "en": "Session name",
        "it": "Nome della sessione"
      },
      "advices": {
        "en": "Terapeutic advices",
        "it": "Consigli terapeutici"
      },
      "tool": {
        "en": "Tool",
        "it": "Attrezzo"
      },
      "exerciseType": {
        "en": "Exercise type",
        "it": "Tipo di allenamento"
      },
      "steps": {
        "en": "Steps",
        "it": "Step"
      },
      "duration": {
        "en": "Duration",
        "it": "Durata"
      },
      "power": {
        "en": "Power",
        "it": "Potenza"
      }
    }
  },
  {
    "identifier": "nutritionalTherapy",
    "type": "therapy",
    "name": {
      "en": "Nutritional Therapy",
      "it": "Terapia Nutrizionale"
    },
    "schema": {
      "type": "object",
      "properties": {
        "nutritionalAdvices": {
          "type": "string"
        },
        "personalAdvices": {
          "type": "string"
        },
        "diet": {
          "type": "string"
        }
      }
    },
    "labels": {
      "nutritionalAdvices": {
        "en": "Advices for a healthy and correct diet",
        "it": "Consigli di una sana e corretta alimentazione"
      },
      "personalAdvices": {
        "en": "Personal recommendations",
        "it": "Consigli personalizzati"
      },
      "diet": {
        "en": "Diet",
        "it": "Dieta"
      }
    },
    "hints": {
      "diet": [
        {
          "en": "Low calorie 1200 KCAL (BEE ≤ 1300KCAL)",
          "it": "Ipocalorica 1200 KCAL (BEE ≤ 1300KCAL)"
        },
        {
          "en": "Low calorie 1400 KCAL (BEE [1400-1600] KCAL)",
          "it": "Ipocalorica 1400 KCAL (BEE [1400-1600] KCAL)"
        },
        {
          "en": "Low calorie 1600 KCAL (BEE [1600-1800] KCAL)",
          "it": "Ipocalorica 1600 KCAL (BEE [1600-1800] KCAL)"
        },
        {
          "en": "Low calorie 1800 KCAL (BEE [1800-2000] KCAL)",
          "it": "Ipocalorica 1800 KCAL (BEE [1800-2000] KCAL)"
        }
      ]
    }
  },
  {
    "identifier": "psychologicalTherapy",
    "type": "therapy",
    "name": {
      "en": "Psychological Therapy",
      "it": "Terapia Psicologica"
    },
    "schema": {
      "type": "object",
      "properties": {
        "advices": {
          "type": "string"
        },
        "hiddenNotes": {
          "type": "string"
        }
      },
      "required": [
        "advices"
      ]
    },
    "labels": {
      "advices": {
        "en": "Terapeutic advices",
        "it": "Consigli terapeutici"
      },
      "hiddenNotes": {
        "en": "Notes (not visible to the patient)",
        "it": "Note (non visibili al paziente)"
      }
    }
  }
]
